import React, {useState} from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState('');
  const [lat, setLat] = useState('');
  const [lon, setLon] = useState('');

  const url = `https://api.openweathermap.org/geo/1.0/direct?q=${location}&limit=5&appid=56f56a0fa15d71cf040938507a7e4091`;

  const searchLocation = (event) => {
    if (event.key === 'Enter') { axios.get(url).then((response) => {
        setData(response.data)
        console.log(data.lat)
      })
      setLocation('')
    }
  }



  return (
    <div className="app">
      <div className="search">
        <input
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder='Enter Location'
          type="text" />
      </div>
       <div className="container">
        <div className="top">
          <div className="location">
            <p>Dallas</p>
          </div>
          <div className="temp">
            <h1>25°</h1>
          </div>
          <div className="description">
            <p>clouds</p>
          </div>
        </div>
        <div className="bottom">
          <div className="feels">
            <p className="bold">27°</p>
            <p>Fells like</p>
          </div>
          <div className="humidity">
            <p className="bold">20%</p>
            <p>Humidity</p>
          </div>
          <div className="wind">
            <p className="bold">15 km/h</p>
            <p>Wind</p>
          </div>
        </div>
       </div>
    </div>
  );
}

export default App;
